$(function(){
	var wc_payment_method = $(".wc_payment_method");
	var payment_box = $(".payment_box");
	var payment_method_cod = $(".payment_method_cod");
	var payment_method_alipay = $(".payment_method_alipay");
	var payment_method_wechatpay = $(".payment_method_wechatpay");
	var place_order = $("#place_order");
	for(var i=wc_payment_method.length-1 ; i>=0 ;i--){
		wc_payment_method.eq(i).on("click",function(){
			var _this = $(this);
			payment_box.hide();
			_this.find(".payment_box").show();
		})
	}
	
	payment_method_cod.on("click",function(){
		place_order.val("下单");
	})

	payment_method_alipay.on("click",function(){
		place_order.val("到支付宝付款");
	})
	
	payment_method_wechatpay.on("click",function(){
		place_order.val("使用微信扫码支付");
	})
})